INSERT INTO `post` VALUES (386, 'ประกวดราคาจ้างบำรุงรักษาเครื่องอ่านและแปลงสัญญาณภาพเอกซเรย์เป็นระบบดิจิตอล พร้อมระบบจัดเก็บและส่งข้อมูลภาพทางการแพทย์ (CRPACS) จำนวน ๑ ระบบ ด้วยวิธีประกวดราคาอิเล็กทรอนิกส์ (e-bidding)', 'กำหนดเสนอราคาในวันที่ 26 กุมภาพันธ์ 2561', '20180215_153317.pdf', '2018-02-15 15:33:17', 'pasadu', '2018-02-15 15:33:17', NULL, NULL, 3, '20180215_153317_spec.pdf', '20180215_153317_price.pdf', 0, 1, NULL, NULL);
INSERT INTO `post` VALUES (387, 'ประกวดราคาจ้างก่อสร้างโครงการพื้นฐานศูนย์การเรียนรู้และสาธิตแพทย์แผนไทยและสมุนไพรไทยต้นแบบ ด้วยวิธีประกวดราคาอิเล็กทรอนิกส์ (e-bidding)', 'ผู้ยื่นข้อเสนอต้องยื่นข้อเสนอและเสนอราคาทางระบบจัดซื้อจัดจ้างภาครัฐด้วยอิเล็กทรอนิกส์ ในวันที่ ๗ มีนาคม ๒๕๖๑ ระหว่างเวลา ๐๘.๓๐ น. ถึง ๑๖.๓๐ น.\r\n                ผู้สนใจสามารถขอซื้อเอกสารประกวดราคาด้วยอิเล็กทรอนิกส์ ในราคาชุดละ ๕,๐๐๐.๐๐ บาท ผ่านทางระบบจัดซื้อจัดจ้างภาครัฐด้วยอิเล็กทรอนิกส์และชำระเงินผ่านทางธนาคาร ตั้งแต่วันที่ ๑๕ กุมภาพันธ์ ๒๕๖๑ ถึงวันที่ ๖ มีนาคม ๒๕๖๑ โดยดาวน์โหลดเอกสารผ่านทางระบบจัดซื้อจัดจ้างภาครัฐด้วยอิเล็กทรอนิกส์ ได้ภายหลังจากชำระเงินเป็นที่เรียบร้อยแล้วจนถึงก่อนวันเสนอราคา\r\n                ผู้สนใจสามารถดูรายละเอียดได้ที่เว็บไซต์ www.cpa.go.th หรือ www.gprocurement.go.th หรือสอบถามทางโทรศัพท์หมายเลข ๐ ๓๗๒๑ ๑๐๘๘ ต่อ ๒๕๓๗ ในวันและเวลาราชการ\r\n                ผู้สนใจต้องการทราบรายละเอียดเพิ่มเติมเกี่ยวกับรายละเอียดและขอบเขตของงาน โปรดสอบถามมายัง จังหวัดปราจีนบุรี ผ่านทางอีเมล์ sommai.k@moph.mail.go.th หรือช่องทางตามที่กรมบัญชีกลางกำหนดภายในวันที่ ๒๗ กุมภาพันธ์ ๒๕๖๑ โดยจังหวัดปราจีนบุรีจะชี้แจงรายละเอียดดังกล่าวผ่านทางเว็บไซต์ www.cpa.go.th และ www.gprocurement.go.th ในวันที่ ๒๘ กุมภาพันธ์ ๒๕๖๑ ', '20180215_163844.pdf', '2018-02-15 16:38:44', 'pasadu', '2018-02-15 16:38:44', NULL, NULL, 3, '20180215_163844_spec.pdf', '20180215_163844_price.pdf', 0, 1, NULL, NULL);
INSERT INTO `post` VALUES (388, 'ประกวดราคาจ้างก่อสร้างอาคารคลอด ผ่าตัด และพักผู้ป่วย เป็นอาคาร คสล. ๕ ชั้น พื้นที่ใช้สอยประมาณ ๑,๗๗๒ ตารางเมตร โรงพยาบาลเจ้าพระยาอภัยภูเบศร ตำบลท่างาม อำเภอเมืองปราจีนบุรี จังหวัดปราจีนบุรี ๑ หลัง ด้วยวิธีประกวดราคาอิเล็กทรอนิกส์ (e-bidding)', 'ผู้สนใจสามารถขอซื้อเอกสารประกวดราคาอิเล็กทรอนิกส์ ผ่านทางระบบจัดซื้อจัดจ้างภาครัฐด้วยอิเล็กทรอนิกส์ ตั้งแต่วันที่ ๑๖ กุม๓าพันธื ๒๕๖๑ ถึงวันที่ ๑๙ มีนาคม ๒๕๖๑ \r\nผู้ยื่นข้อเสนอต้องยื่นข้อเสนอและเสนอราคาทางระบบจัดซื้อจัดจ้างภาครัฐด้วยอิเล็กทรอนิกส์ ในวันที่ ๒๐ มีนาคม ๒๕๖๑ ระหว่างเวลา ๐๘.๓๐ น. ถึง ๑๖.๓๐ น.', '20180216_235911.pdf', '2018-02-16 23:59:11', 'pasadu', '2018-02-16 23:59:11', NULL, NULL, 3, '20180216_235911_spec.pdf', '20180216_235911_price.pdf', 0, 1, NULL, NULL);
